          if(r >= 0x200) {
            fl_alert( "%s", _("Show Profile failed. iccexamin not found") );
            fprintf(stderr, "%s:%d command \"%s\" failed with error: %d.\n",
                    __FILE__,__LINE__, command, r);
          }
