void listWindow( Fl_Widget *w ) {
  Fl_Group *g = dynamic_cast <Fl_Group*> (w);
  
    static int level = 0;
  
  
    if( g )
    {
      int n = g->children();
  
      std::cout << std::endl;
      for(int i = 0; i < level; ++i) std::cout << " ";
  
      std::cout << "Group t" << (int)w->type() << " " << w->x()<<"+"<<w->y()<<"+"<<w->w()<<"x"<<w->h() <<
                   " \"" << (w->label()?w->label():"") << "\":";
  
      ++level;
      for( int i = 0; i < n; ++i )
      {
        listWindow( g->child(i) );
      }
      --level;
      std::cout << std::endl;
    } else
    {
      std::cout << " t" << (int)w->type() << " " << w->x()<<"+"<<w->y()<<"+"<<w->w()<<"x"<<w->h() <<
                   " " << (w->label()?w->label():"- ");
    }
}

